import React from 'react';
import { CSSTransition } from 'react-transition-group';
import './SlideContainer.css';

function SlideContainer({ isVisible, children }) {
  return (
    <CSSTransition
      in={isVisible}
      timeout={300}
      classNames="slide"
      unmountOnExit
    >
      <div className="slide-container">
        {children}
      </div>
    </CSSTransition>
  );
}

export default SlideContainer;
